    private static long countLinesInDirectory(File directory) {
        long count = 0;
        File[] files = directory.listFiles();

        if (files == null) return 0;

        for (File file : files) {
            if (file.isDirectory()) {
                count += countLinesInDirectory(file); // 递归处理子文件夹
            } else if (isTextFile(file)) {
                count += countLinesInFile(file);
            }
        }
        return count;
    }

    private static boolean isTextFile(File file) {
        String fileName = file.getName().toLowerCase();
        for (String ext : TEXT_EXTENSIONS) {
            if (fileName.endsWith("." + ext)) {
                return true;
            }
        }
        return false;
    }

    private static long countLinesInFile(File file) {
        long lineCount = 0;
        try (BufferedReader reader = new BufferedReader(new FileReader(file))) {
            while (reader.readLine() != null) {
                lineCount++;
            }
            System.out.println(file.getAbsolutePath() + " - " + lineCount + " 行");
        } catch (IOException e) {
            System.err.println("读取文件失败: " + file.getAbsolutePath() + " - " + e.getMessage());
        }
        return lineCount;
    }
